/** @public */
export type MlsMessageContent = MlsWelcome | MlsPrivateMessage | MlsGroupInfo | MlsKeyPackage | MlsPublicMessage;
/** @public */
export type MlsMessage = MlsMessageProtocol & MlsMessageContent;
export declare const mlsPublicMessageEncoder: Encoder<MlsPublicMessage>;
export declare const mlsWelcomeEncoder: Encoder<MlsWelcome>;
export declare const mlsPrivateMessageEncoder: Encoder<MlsPrivateMessage>;
export declare const mlsGroupInfoEncoder: Encoder<MlsGroupInfo>;
